package Arrays_PAck;

public class Numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Reverse 2 number without 
		int a=10,b=20,c = 0;
		System.out.println("Before swap:"+"a:"+a+"b:"+b);
		c=a+b;//30
		a=c-a;//20
		b=c-a;//10
		
		System.out.println("After swap:"+"a:"+a+"b:"+b);
		
		

	}

}
